mapper.select:
SELECT MBR_ID 
FROM MEMBER_TBL 
WHERE MBR_ID = #{mbr_id}

mapper.update:
	UPDATE MEMBER_TBL 
	SET MBR_CNTN_DATE = SYSDATE
    WHERE MBR_ID = #{mbr_id}

	UPDATE MEMBER_TBL 
	SET MBR_PW = #{enc_mbr_pw}
    WHERE MBR_ID = #{mbr_id}

UPDATE MEMBER_TBL SET MBR_NM = #{mbr_nm},
MBR_PW = #{mbr_pw},
MBR_ZIP = #{mbr_zip}, MBR_ADDR = #{mbr_addr}, MBR_DADDR = #{mbr_daddr}, MBR_TELNO = #{mbr_telno}, MBR_EML_ADDR = #{mbr_eml_addr}, MBR_EML_ADDR_YN = #{mbr_eml_addr_yn}, WHERE MBR_ID = #{mbr_id}
